def build_languages():
    languages = []
    languages_native = []
    languages_english = []

    if not babel:  # pragma: no cover
        return languages, languages_native, languages_english

    locales = babel.localedata.locale_identifiers()
    locales.sort()
    for l_id in locales:
        locale = babel.Locale(l_id)
        if locale.english_name:
            languages_english.append((l_id, _(locale.english_name)))
            if locale.display_name:
                languages_native.append((l_id, locale.display_name))

                if locale.display_name == locale.english_name:
                    label = '{0}'.format(locale.english_name)
                else:
                    label = '{0} ({1})'.format(
                        locale.display_name,
                        locale.english_name
                    )
                languages.append((l_id, label))
    return languages, languages_native, languages_english

LANGUAGES, LANGUAGES_NATIVE, LANGUAGES_ENGLISH = build_languages()


class Country(models.Model):
    """
    Country model
    =============

    Stores 2-letter ISO country code 2-letter continent code
    """

    code = models.CharField(
        _('country'),
        max_length=2,
        choices=COUNTRIES,
        unique=True
    )
    continent = models.CharField(
        _('continent'),
        max_length=2,
        choices=CONTINENTS
    )

    def __unicode__(self):  # pragma: no cover
        return self.get_code_display()

    def __str__(self):  # pragma: no cover
        return self.get_code_display()

    class Meta:
        verbose_name = _('country')
        verbose_name_plural = _('countries')
